package main

import "fmt"

func main() {
	packetIndex := startOfPacketIndex(input, 4)
	fmt.Println(packetIndex)
	packetIndex = startOfPacketIndex(input, 14)
	fmt.Println(packetIndex)
}

func startOfPacketIndex(s string, distinctChars int) int {
	for i := 0; i < len(s)-(distinctChars+1); i++ {
		lookup := map[rune]interface{}{}

		for j := 0; j < distinctChars; j++ {
			c := rune(s[i+j])
			if _, ok := lookup[c]; ok {
				break
			} else {
				lookup[c] = nil
			}
		}

		if len(lookup) == distinctChars {
			return i + distinctChars
		}
	}

	panic("Not found")
}

